import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
  const [formData, setFormData] = useState({
    nombre: '',
    nombre_usuario: '',
    email: '',
    password: '',
    rol: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/usuarios/registrar', formData);
      console.log('Registro exitoso:', response.data);
      // Maneja el éxito (por ejemplo, redirigir al usuario, mostrar un mensaje, etc.)
    } catch (error) {
      console.error('Error en el registro:', error.response.data);
      // Maneja el error (por ejemplo, mostrar un mensaje de error al usuario)
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="nombre"
        value={formData.nombre}
        onChange={handleChange}
        placeholder="Nombre"
        required
      />
      <input
        type="text"
        name="nombre_usuario"
        value={formData.nombre_usuario}
        onChange={handleChange}
        placeholder="Nombre de usuario"
        required
      />
      <input
        type="email"
        name="email"
        value={formData.email}
        onChange={handleChange}
        placeholder="Correo electrónico"
        required
      />
      <input
        type="password"
        name="password"
        value={formData.password}
        onChange={handleChange}
        placeholder="Contraseña"
        required
      />
      <input
        type="text"
        name="rol"
        value={formData.rol}
        onChange={handleChange}
        placeholder="Rol"
        required
      />
      <button type="submit">Registrar</button>
    </form>
  );
};

export default Register;
