import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Form, Card } from 'react-bootstrap';
import { registerUser } from '../api/auth';

const Register = () => {
  const [nombre, setNombre] = useState('');
  const [nombreUsuario, setNombreUsuario] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [rol, setRol] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      alert("Las contraseñas no coinciden.");
      return;
    }

    try {
      const userData = { nombre, nombre_usuario: nombreUsuario, email, password, rol };
      const result = await registerUser(userData);
      console.log('Registro exitoso:', result);
      navigate('/login');
    } catch (error) {
      console.error('Error en el registro:', error);
      alert('Hubo un problema al registrarse. Por favor, intente de nuevo.');
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <Card style={{ width: '400px', padding: '20px', boxShadow: '0 10px 8px rgba(0, 0, 0, 0.1)' }}>
        <h2 className="text-center mb-4">Registrarse</h2>

        <Form onSubmit={handleSubmit}>
          {/* Form fields */}
          <Form.Group>
            <Button type="submit">Registrarse</Button>
            <Button variant="secondary" type="button" onClick={() => navigate('/login')}>Volver a Iniciar Sesión</Button>
          </Form.Group>
        </Form>
      </Card>
    </div>
  );
};

export default Register;
