import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Button, Form, Card} from "react-bootstrap";



const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('')
  const navigate = useNavigate();
  

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Limpiar errores previos

    try {
      // Petición POST con Axios
      const response = await axios.post('http://localhost:3000/api/login', {
        email: email,
        password: password
      });

      if (response.status === 200) {
        // Guardar el token en localStorage
        localStorage.setItem('token', response.data.token);

        // Redirigir al dashboard
        navigate('/dashboard');
      }
    } catch (err) {
      // Mostrar el mensaje de error en caso de fallo
      if (err.response && err.response.data) {
        setError(err.response.data.message || 'Error en la autenticación');
      } else {
        setError('Error en la conexión con el servidor');
      }
    }
  };

  const handleRegisterRedirect = () => {
    navigate('/register');
  };
  return (

    
    <div className="d-flex justify-content-center align-items-center vh-100">
    <Card style={{  width: '400px', padding: '20px', boxShadow: '0 10px 8px rgba(0, 0, 0, 0.1)' }}>
        <h2 className="text-center mb-4">Iniciar Sesión</h2>

    <Form onSubmit={handleSubmit}>
      <Form.Group id="correo"> 
        <Form.Label htmlFor="email">Correo:</Form.Label>
        <Form.Control
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </Form.Group>

      <Form.Group id="contrasena">
        <Form.Label htmlFor="password">Contraseña:</Form.Label>
        <Form.Control
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
      </Form.Group>

      <Form.Group id='botones'>
        <Button variant="primary" type="submit">Iniciar</Button>
        <Button variant="secondary" type="button" onClick={handleRegisterRedirect} className="ms-2">
          Registrarse
        </Button>
      </Form.Group>
    </Form> 
    </Card>
  </div>
);
};

export default Login;
