// src/api/auth.js
import axios from '../axios'; // Asegúrate de que la ruta sea correcta

export const registerUser = async (userData) => {
  try {
    const response = await axios.post('/register', userData); // La URL aquí es relativa a baseURL
    return response.data;
  } catch (error) {
    console.error('Error en el registro', error);
    throw error;
  }
};

export const loginUser = async (credentials) => {
  try {
    const response = await axios.post('/login', credentials); // La URL aquí es relativa a baseURL
    return response.data;
  } catch (error) {
    console.error('Error en el inicio de sesión', error);
    throw error;
  }
};
